import React, { useState } from 'react'
import './navSide.css';



const NavSide = (props) => {
    const [inValue, setInValue] = useState(-50);
    const [panValue, setPanValue] = useState(0);
    return (
        <>
            <div className = 'NavSide'>
                <div className = 'sliderContainer'> 
                    <div className = 'range'>
                        <div className ="Vol">Volume: {inValue}</div>
                        <div className='slide'>
                            <div className = 'field'>
                                <div className= 'value left'>0</div>
                                <input className='slider' 
                                    type="range" 
                                    min ='100'max='0' 
                                    onChange={(e => {
                                        props.handleVol(e.target.value);
                                        setInValue(e.target.value);
                                    })}
                                    value={inValue}
                                    steps ='1'>
                                </input>
                                {props.handleVol(-50)}
                                <div className= 'value right'>100</div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className = 'sliderContainer two'> 
                    <div className = 'range'>
                        <div className ="Vol">Pan: {panValue}</div>
                        <div className='slide'>
                            <div className = 'field'>
                                <div className= 'value left'>0</div>
                                <input className='slider' 
                                    type="range" 
                                    min ='-100'max='100' 
                                    onChange={(e => {
                                        props.handlePan(e.target.value);
                                        setPanValue(e.target.value);
                                    })}
                                    value={panValue}
                                    steps ='1'>
                                </input>
                                {props.handlePan(0)}
                                <div className= 'value right'>100</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div> 
        </>
    )
}

export default NavSide;



