import React, { useState } from 'react'
import PianoRoll from './MusicComponents/PianoRoll'
import Night from '../sources/night.mp4'
import './Home.css'
import Nav from './nav.js';
import NavSide from './navSide';

const Home = () => {
    const [BPM, setBPM] = useState(75);

    const handleIncrement = () => {
        if(BPM < 200){
            setBPM(BPM => BPM + 1)
            console.log({BPM})
        }
    }
    const handleDecrement = () => {
        if(BPM > 0){
            setBPM(BPM => BPM - 1)
            console.log({BPM})
        }
    }

    const [volume, setVolume] = useState(-50);
    const handleVolume = e => {
        console.log(volume);
        setVolume(e);
    }

    const [pan, setPan] = useState(50);
    const handlePan = e => {
        setPan(e);
    }

    const [toggle, setToggle] = useState(false);
    const updateToggle = () =>{
        setToggle(!toggle);
    }
    const [isPlaying,setIsPlaying] = useState(false);


    return (
        <>
            <Nav increment={handleIncrement} decrement={handleDecrement} bpm={BPM} tog={updateToggle} play={isPlaying} setPlay={setIsPlaying}/>
            <NavSide handleVol={handleVolume} vol={volume} handlePan={handlePan} pan={pan   } />
            <div className='Container'>
                <div className='videoContainer' > 
                    <video className='Video' autoPlay loop muted src={Night} type ='video/mp4' />
                </div>
                <PianoRoll bpm={BPM} vol={volume} pan={pan} togg={toggle} play={isPlaying} setPlay={setIsPlaying} />
            </div>
        </>
    )
}

export default Home
